  ...formData,
  submittedAt: new Date().toISOString(),
  ipAddress: $input.item.json.headers['x-forwarded-for'] || $input.item.json.headers['x-real-ip'] || 'unknown',
  userAgent: $input.item.json.headers['user-agent'] || 'unknown'
};

